package br.com.myCompany;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;@Controllerpublic class PedidoController {    @Autowired    private RestauranteRepository repository;    @RequestMapping(value = "/pedido/{id}", method = RequestMethod.POST)    @ResponseBody    public void fazerPedido(@RequestBody Pedido pedido, @PathVariable Long id) {        CardapioItem item = repository.getItem(id);        pedido.setItem(item);        repository.salvarPedido(pedido);    }}